$k_stmts[] = "INSERT INTO ".K_TBL_DATA_TEXT." (page_id, field_id, value, search_value) VALUES (78, 84, '<p>\\r\\n	This is an opportunity for an experienced hardware and firmware engineer to help develop our next generation products and technologies and advance our existing products. We are looking for a motivated individual, with up-to-date technical skills, who can design and implement embedded hardware and firmware to support our products for a variety of OEMs. The candidate should have knowledge of control methods for power systems and should be confident and capable of working on his / her own but also able to interact with other team members and provide oral and written reports to management. This is a hands-on position for product development with substantial growth opportunities.</p>\\r\\n', 'This is an opportunity for an experienced hardware and firmware engineer to help develop our next generation products and technologies and advance our existing products. We are looking for a motivated individual, with up-to-date technical skills, who can design and implement embedded hardware and firmware to support our products for a variety of OEMs. The candidate should have knowledge of control methods for power systems and should be confident and capable of working on his / her own but also able to interact with other team members and provide oral and written reports to management. This is a hands-on position for product development with substantial growth opportunities.');";
$k_stmts[] = "INSERT INTO ".K_TBL_DATA_TEXT." (page_id, field_id, value, search_value) VALUES (78, 85, '<ul>\\r\\n	<li>\\r\\n		Key contributor to the firmware and hardware development on next-generation and existing platforms</li>\\r\\n	<li>\\r\\n		Embedded firmware development using C/C++</li>\\r\\n	<li>\\r\\n		Participate in system architecture, design and implementation&nbsp;</li>\\r\\n	<li>\\r\\n		Setup and maintain integrated development environments</li>\\r\\n	<li>\\r\\n		Assist in the electrical design of power, analog, and digital circuits</li>\\r\\n	<li>\\r\\n		Provide oral and written reports to management</li>\\r\\n	<li>\\r\\n		Participate in selection and qualification of microcontrollers and tools needed for product development</li>\\r\\n</ul>\\r\\n', 'Key contributor to the firmware and hardware development on next-generation and existing platforms Embedded firmware development using C/C++ Participate in system architecture, design and implementation Setup and maintain integrated development environments Assist in the electrical design of power, analog, and digital circuits Provide oral and written reports to management Participate in selection and qualification of microcontrollers and tools needed for product development');";
$k_stmts[] = "INSERT INTO ".K_TBL_DATA_TEXT." (page_id, field_id, value, search_value) VALUES (78, 97, '', '');";
$k_stmts[] = "INSERT INTO ".K_TBL_DATA_TEXT." (page_id, field_id, value, search_value) VALUES (78, 86, '<ul>\\r\\n	<li>\\r\\n		Detailed knowledge of embedded microcontrollers such as Texas Instruments, Microchip and Silicon Laboratories Microcontrollers</li>\\r\\n	<li>\\r\\n		Strong knowledge of C/C++</li>\\r\\n	<li>\\r\\n		Experience with control and regulation of power supplies</li>\\r\\n	<li>\\r\\n		Implementation of closed loop and PID loops for power and voltage control in power supplies</li>\\r\\n	<li>\\r\\n		Strong familiarity with debugging tools</li>\\r\\n	<li>\\r\\n		Experience in developing projects on resource-constrained embedded systems</li>\\r\\n	<li>\\r\\n		Familiarity with common interfaces (I2C, USB, SPI,etc)</li>\\r\\n	<li>\\r\\n		Excellent trouble-shooting skills</li>\\r\\n	<li>\\r\\n		Excellent written and oral communication skills</li>\\r\\n	<li>\\r\\n		Must be able to handle multiple projects and products concurrently</li>\\r\\n	<li>\\r\\n		Must be local</li>\\r\\n</ul>\\r\\n', 'Detailed knowledge of embedded microcontrollers such as Texas Instruments, Microchip and Silicon Laboratories Microcontrollers Strong knowledge of C/C++ Experience with control and regulation of power supplies Implementation of closed loop and PID loops for power and voltage control in power supplies Strong familiarity with debugging tools Experience in developing projects on resource-constrained embedded systems Familiarity with common interfaces (I2C, USB, SPI,etc) Excellent trouble-shooting skills Excellent written and oral communication skills Must be able to handle multiple projects and products concurrently Must be local');";
$k_stmts[] = "INSERT INTO ".K_TBL_DATA_TEXT." (page_id, field_id, value, search_value) VALUES (78, 87, '<ul>\\r\\n	<li>\\r\\n		BS/MS in Electrical Engineering or Computer Science or related field</li>\\r\\n	<li>\\r\\n		Min 7 Years of related embedded development experience</li>\\r\\n</ul>\\r\\n', 'BS/MS in Electrical Engineering or Computer Science or related field Min 7 Years of related embedded development experience');";
